import logoImage from '../images/jerlogo.png';
import virtualPet from '../images/virtual-pet.png';
import monsterKong from '../images/monster-kong.png';
import spanishLearning from '../images/spanish-learning.png';
import towerDefense from '../images/tower-defense.png';

const GameBlock = (props) => (
  <div>
    <img src={props.image} alt="game image" />
    <div>
      <h2>{props.title}</h2>
      <p>{props.description}</p>
    </div>
  </div>
)

export default () => (
  <div>
    <header>
      <img src={logoImage} alt="logo" id="logoImage" />
    </header>
    <h1>My Games Page</h1>
    <GameBlock
      image={virtualPet}
      title="Virtual Pet"
      description="Balance your pet’s health and happiness by feeding it healthy and unhealthy food, and provide it with entertainment with simple yet fun to play mechanics.

      If your pet's health gets too low, use the potion to save it's life at the expense of fun."
    />
    <GameBlock
      image={monsterKong}
      title="Virtual Pet"
      description="Balance your pet’s health and happiness by feeding it healthy and unhealthy food, and provide it with entertainment with simple yet fun to play mechanics.

      If your pet's health gets too low, use the potion to save it's life at the expense of fun."
    />
    <GameBlock
      image={spanishLearning}
      title="Virtual Pet"
      description="Balance your pet’s health and happiness by feeding it healthy and unhealthy food, and provide it with entertainment with simple yet fun to play mechanics.

      If your pet's health gets too low, use the potion to save it's life at the expense of fun."
    />
    <GameBlock
      image={towerDefense}
      title="Virtual Pet"
      description="Balance your pet’s health and happiness by feeding it healthy and unhealthy food, and provide it with entertainment with simple yet fun to play mechanics.

      If your pet's health gets too low, use the potion to save it's life at the expense of fun."
    />

    <style jsx>{`
      header {
        background-color: lightgrey;
        height: 100px;
        text-align: center;
        padding: 15px;
      }

      #logoImage {
        height: 100px;
        border: 1px ridge lightgrey;
        border-radius: 10px;
      }

      h1 {
        text-align: center;
      }
    `}</style>
  </div>
)
